function fetchQuestion() {
    function keepOnlyChinese(str) {
        return str.replace(/[^\u4e00-\u9fa5]/g, '');
    }
    const questionType = document.querySelector('.type').innerText.replace(/\s+/g, '');
    let questionContent = document.querySelector('.content').innerText.replace(/^\d+、/, '').trim();
    const chineseOnlyQuestion = keepOnlyChinese(questionContent);
   
    let currentPrefix;
    if (chineseOnlyQuestion.length > 15) {
        currentPrefix = chineseOnlyQuestion.slice(0, 15);
    } else {
        currentPrefix = chineseOnlyQuestion;
    }

    let matchedAnswer = null;
    for (const q in tk_json) {
        const chineseOnlyQ = keepOnlyChinese(q);
        let qPrefix;
        if (chineseOnlyQ.length > 15){
            qPrefix = chineseOnlyQ.slice(0, 15);
        } else {
            qPrefix = chineseOnlyQ;
        }
        if (qPrefix === currentPrefix){
            matchedAnswer = tk_json[q];
            break;
        }
    }

    if (!matchedAnswer) {
        console.log('未找到匹配题目');
        return;
    }

    if (questionType === "多选题") {
        const options = Array.from(document.querySelectorAll('.option')).map((option, index) => {
            const label = option.querySelector('.el-checkbox__label').innerText.trim();
            const value = label.charAt(0);
            const checkboxInput = option.querySelector('.el-checkbox__original');

            setTimeout(() => {
                if (matchedAnswer.includes(value)) {
                    checkboxInput.checked = true;
                    checkboxInput.dispatchEvent(new Event('change', {bubbles: true}));
                }
            }, index * 100);
        });
    } else {
        const options = Array.from(document.querySelectorAll('.option')).map(option => {
            const label = option.querySelector('.el-radio__label').innerText.trim();
            const value = label.charAt(0);
            const radioInput = option.querySelector('.el-radio__original');

            if (matchedAnswer === value) {
                radioInput.checked = true;
                radioInput.dispatchEvent(new Event('change', {bubbles: true}));
            }
        });
    }
    console.log(`本程序由盐泡(徐大墙管理组)编写 Fuck → ${count}|100`);
    count++;
}

